'use strict';
var merge = require('../merge');

describe('merge', function() {
  it('does the right thing', function() {
    var original = {a: 1};
    expect(merge(original, {a: 2, b: 1}, {a: 3, c: 2})).toEqual({
      a: 3,
      b: 1,
      c: 2
    });
    expect(original).toEqual({a: 1});
  });
});
